<?php require_once __DIR__ . '/src/auth.php'; require_login(); if($_SERVER['REQUEST_METHOD']==='POST'){ $sender = $_SESSION['user_id']; $receiver = (int)($_POST['receiver_id'] ?? 0); if($sender && $receiver){ $pdo->prepare('INSERT INTO interests (sender_id, receiver_id, status, created_at) VALUES (?,?,?,NOW())')->execute([$sender, $receiver, 'Pending']); } } header('Location: profile.php?id='.$receiver); exit; ?>